(() => {
    "use strict";
    !(function (e) {
        let t = new Image();
        (t.onload = t.onerror = function () {
            1 == (2 == t.height) ? document.querySelector("body").classList.add("webp") : document.querySelector("body").classList.add("no-webp");
        }),
            (t.src = "data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA");
    })(),
        (function () {
            let e = document.querySelectorAll("[data-lg]"),
                t = document.querySelector(".header__toggle-language");
            t.addEventListener("click", () => {
                e.forEach((e) => {
                    t.checked ? (e.textContent = e.dataset.enLg) : (e.textContent = e.dataset.uaLg);
                });
            });
        })(),
        (function () {
            let e = document.querySelector(".top-right");
            e.addEventListener("mouseover", (e) => e.target.classList.add("active-card")), e.addEventListener("mouseout", (e) => e.target.classList.remove("active-card"));
        })(),
        (function () {
            let e = document.querySelector(".candle__cards__grid"),
                t = document.querySelectorAll(".candle__card");
            function c(e) {
                let c = e.target.parentElement;
                if (c.classList.contains("candle__card")) {
                    for (let e = 0; e < t.length; e++) t[e].classList.remove("active-card");
                    c.classList.add("active-card");
                }
            }
            e.addEventListener('touchstart', c)
            e.addEventListener("mouseover", c),
                e.addEventListener("click", c),
                e.addEventListener("mouseleave", () => {
                    for (let e = 0; e < t.length; e++) t[e].classList.remove("active-card");
                });
        })(),
        (function () {
            let e = document.querySelector(".choose__list"),
                t = document.querySelectorAll(".choose__item");
            e.addEventListener("click", (e) => {
                let c = e.target;
                if (c.classList.contains("choose__item")) {
                    for (let e = 0; e < t.length; e++) t[e].classList.remove("active-choose-item");
                    c.classList.add("active-choose-item");
                }
                let a = document.querySelectorAll(".candle__card"),
                    o = document.querySelectorAll("[data-size-small]"),
                    l = document.querySelectorAll("[data-size-big]");
                switch ((a.forEach((e) => (e.style.display = "none")), c.dataset.size)) {
                    case "big":
                        l.forEach((e) => {
                            e.style.display = "grid";
                        });
                        break;
                    case "small":
                        o.forEach((e) => {
                            e.style.display = "grid";
                        });
                        break;
                    default:
                        a.forEach((e) => {
                            e.style.display = "grid";
                        });
                }
            });
        })();
    const e = document.querySelectorAll('a[href*="#"]');
    for (let t of e)
        t.addEventListener("click", function (e) {
            e.preventDefault();
            const c = t.getAttribute("href").substr(1);
            document.getElementById(c).scrollIntoView({ behavior: "smooth", block: "start" });
        });
})();

