function testWebP(callback) {
        let webP = new Image();
        webP.onload = webP.onerror = function () {
            callback(webP.height == 2);
        };
        webP.src = "data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA";
    }
    testWebP(function (support) {
        if (support == true) {
            document.querySelector('body').classList.add('webp');
        }else{
            document.querySelector('body').classList.add('no-webp');
        }
    });
testWebP();

function topCard() {
    let parentCard = document.querySelector('.top-right')
    parentCard.addEventListener('mouseover', e => e.target.classList.add('active-card'))
    parentCard.addEventListener('mouseout', e => e.target.classList.remove('active-card'))
}
topCard();

function filterChoose() {
    let filter = document.querySelector('.choose__list')
    let filterItems = document.querySelectorAll('.choose__item')

    filter.addEventListener('click', (e) => {
        let target = e.target

        if(target.classList.contains('choose__item')) {
            for(let i = 0; i < filterItems.length; i++) {
                filterItems[i].classList.remove('active-choose-item');
            }
            target.classList.add('active-choose-item');
        }

        let allCandle = document.querySelectorAll('.candle__card')
        let smallCandle = document.querySelectorAll('[data-size-small]')
        let bigCandle = document.querySelectorAll('[data-size-big]')

        allCandle.forEach(candle => candle.style.display = 'none')

        switch (target.dataset.size){
            case 'big':
                bigCandle.forEach(candle => {
                    candle.style.display = 'grid'
                });
                break
            case 'small':
                smallCandle.forEach(candle => {
                    candle.style.display = "grid"
                })
                break
            case 'all':
                allCandle.forEach(candle => {
                    candle.style.display = "grid"
                })
                break
            default:
                allCandle.forEach(candle => {
                    candle.style.display = "grid"
                })
        }
    })
}
filterChoose();

function changeChoose() {
    let chooseParent = document.querySelector('.candle__cards__grid')
    let chooseCards = document.querySelectorAll('.candle__card')

    chooseParent.addEventListener('mouseover', changeCard)
    chooseParent.addEventListener('click', changeCard)
    chooseParent.addEventListener('mouseleave', () => {
        for(let i = 0; i < chooseCards.length; i++) {
            chooseCards[i].classList.remove('active-card');
        }
    })
    function changeCard(e){
        let target = e.target.parentElement;
        console.log(target)
        if(target.classList.contains('candle__card')) {
            for(let i = 0; i < chooseCards.length; i++) {
                chooseCards[i].classList.remove('active-card');
            }
            target.classList.add('active-card');
        }
    }
}
changeChoose();
function changeLanguage() {
    let dataLang = document.querySelectorAll('[data-lg]')
    let switchLanguage = document.querySelector(".header__toggle-language")
    switchLanguage.addEventListener('click', () => {
        dataLang.forEach(data => {
            if(switchLanguage.checked){
                data.textContent = data.dataset.enLg
            }
            else{
                data.textContent = data.dataset.uaLg
            }
        })

    })
}
changeLanguage();

function navToAnchor () {
  const anchors = document.querySelectorAll('a[href*="#"]')

    for (let anchor of anchors) {
        anchor.addEventListener('click', function (e) {
            e.preventDefault()

            const blockID = anchor.getAttribute('href').substr(1)

            document.getElementById(blockID).scrollIntoView({
                behavior: 'smooth',
                block: 'start'
            })
        })
    }
}
navToAnchor()
